import PropTypes from "prop-types";
import { useState } from "react";

import { CustButton } from "../../../../components";

export const PerformWorkout = ({
  next,
  prev,
  step,
  goal,
  currentWorkoutSets,
  setCurrentWorkoutSets,
  isCardio,
}) => {
  console.log(
    next,
    prev,
    step,
    goal,
    currentWorkoutSets,
    setCurrentWorkoutSets,
    isCardio
  );
  const [currReps, setCurrReps] = useState(0);

  return (
    <div className="d-block pt-5">
      <h2>Set {currentWorkoutSets}</h2>
      <h3 className="mt-3">
        Goal: {goal.min_reps} - {goal.max_reps}
      </h3>
      <div className="my-5">
        <h3>Enter Reps</h3>
        <input
          value={currReps}
          onChange={(e) => setCurrReps(e.target.value)}
          type="number"
          pattern="[0-9]*"
          inputMode="numeric"
          className="mt-1 TransparentInput white"
        ></input>
      </div>
      <CustButton className="btn-lg w-50" text="COMPLETE SET" color="yellow" />
    </div>
  );
};

PerformWorkout.propTypes = {
  next: PropTypes.func,
  prev: PropTypes.func,
  step: PropTypes.number,
  goal: PropTypes.object,
  currentWorkoutSets: PropTypes.number,
  setCurrentWorkoutSets: PropTypes.func,
  isCardio: PropTypes.bool,
};
