import PropTypes from "prop-types";
import { useState, useMemo } from "react";
import { IoArrowBackCircleSharp } from "react-icons/io5";
import { CustButton, WorkoutItem } from "../../../../components";

export const SelectWorkout = ({
  selectedDay,
  next,
  prev,
  setSelectedDay,
  completedWorkouts,
  setCompletedWorkouts,
}) => {

  useMemo(() => {
    // add new attribute to selectedDay var
    for (var indx in selectedDay.workouts_needed) {
      var item = selectedDay.workouts_needed[indx];
      item.isDone = false;
    }

    // add isDone attribute to cardio object if present
    // will always use 0th index for cardio
    if (selectedDay.cardio?.name) {
      selectedDay.cardio.isDone = false;
      setIsWorkoutDone(workoutDone.push(false));
    }
  }, [selectedDay.workouts_needed]);

  const returnToSecondStep = () => {
    // add alert first to make sure
    if (confirm("Are you sure you want to abandon your current workout?")) {
      // update step, setCompletedWorkouts, selectedDaystate
      setSelectedDay(null);
      prev();
    }
  };

  const isWorkoutFinished = () => {
    for (var i = 0; i < workoutDone.length; i++) {
      if (!workoutDone[i]) {
        return false;
      }
    }
    return true;
  };

  const continueToFourthStep = () => {
    next();
  };

  return (
    <div className="d-block pt-5">
      <div
        onClick={() => returnToSecondStep()}
        className="text-start back-hover back-width pb-1"
      >
        <IoArrowBackCircleSharp color="#EBAA28" size={30} />
      </div>
      <h2 className="fw-bold pb-2">{`Today's Workout`}</h2>
      <h3 className="fw-bold pb-4 yellow">{selectedDay.dayTitle}</h3>
      <div>
        {selectedDay?.cardio?.name && (
          <WorkoutItem
            key={0}
            itemText={selectedDay.cardio.name}
            isComplete={selectedDay.cardio.isDone}
            onClick={() => continueToFourthStep()}
          />
        )}
        {selectedDay.workouts_needed.map((wo, indx) => {
          console.log(wo, indx + 1);
          return (
            <WorkoutItem
              key={indx + 1}
              itemText={wo.workout_name[0]}
              isComplete={wo.isDone}
              onClick={() => continueToFourthStep()}
            />
          );
        })}
      </div>
      <br />
      <div className="py-5">
        <CustButton
          disabled={isWorkoutFinished()}
          link={"/home"}
          className="btn-lg col-8 col-sm-6 col-md-6 col-lg-6 col-xl-6"
          color="yellow"
          text="COMPLETE SESSION"
        />
      </div>
    </div>
  );
};

SelectWorkout.propTypes = {
  selectedDay: PropTypes.object,
  next: PropTypes.func,
  prev: PropTypes.func,
  setSelectedDay: PropTypes.func,
  completedWorkouts: PropTypes.arrayOf(PropTypes.bool),
  setCompletedWorkouts: PropTypes.func,
};
