import PropTypes from "prop-types";
import { CountdownCircleTimer } from "react-countdown-circle-timer";

import "./TimerMode.scss";

const renderTime = ({ remainingTime }) => {
  if (remainingTime === 0) {
    return <div className="timer">Continue Workout!</div>;
  }
  const mins = Math.floor((remainingTime % 3600) / 60);
  const secs = remainingTime % 60;

  return (
    <div className="timer">
      <div className="value">{`${mins}:${secs}`}</div>
      <div className="text">seconds</div>
    </div>
  );
};

export const TimerMode = ({ prev, restTimeSecs }) => {
  // use prev(2) to navigate back to Rest.jsx
  return (
    <>
      <h2 className="fw-bold my-5">Resting Time</h2>
      <div className="timer-wrapper">
        <CountdownCircleTimer
          size={300}
          strokeWidth={16}
          isPlaying={true}
          colors={["#EB4828", "#EB286A", "#EBAA28", "#6E9887", "#386641"]}
          colorsTime={[
            restTimeSecs / 4,
            (restTimeSecs / 4) * 3,
            (restTimeSecs / 4) * 2,
            restTimeSecs / 4,
            0,
          ]}
          duration={restTimeSecs}
          onComplete={() => prev(2)}
        >
          {renderTime}
        </CountdownCircleTimer>
      </div>
    </>
  );
};

TimerMode.propTypes = {
  prev: PropTypes.func,
  restTimeSecs: PropTypes.number,
};
